mapper.select:
SELECT b.board_no, b.board_title, b.board_content, b.board_date, a.user_id as board_writer FROM board b LEFT JOIN auth a ON b.board_writer = a.user_no WHERE b.board_status = 'Y'
AND b.board_title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY b.board_no DESC LIMIT #{boardLimit} OFFSET #{startRow}

mapper.update:
UPDATE board
SET board_title=#{boardTitle}, board_content=#{boardContent}
WHERE board_no=#{boardNo}
